import React, { createContext, useEffect, useState } from "react";

export type ReceiverObjectProps = {
  id: number;
  name: string;
  IP: string;
  device: string;
  type: string;
  isSelected: string;
}

export type ReceiverListContextType = {
  receiverList: ReceiverObjectProps[] | undefined;
  updateReceiverListState: (newState: ReceiverObjectProps[]) => void;
}

export const ReceiverListContext = createContext({
  deviceList: [],
  updateReceiverListState: () => {}, 
});


type ReceiverListProps = {
  children: React.ReactNode;
}

export const ReceiverListProvider = (props: ReceiverListProps) => {
  const [receiverList, setDeviceList] = useState<ReceiverObjectProps[] | undefined>();

  const updateReceiverListState = (newState: ReceiverObjectProps[]) => {
    setReceiverList(newState)
  }

  useEffect(() => {
    fetch(`${process.env.REACT_APP_BACKEND_URL}`, {
      method: "GET", 
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data)
        setDeviceList(data)
      })
      .catch((error) => {
        console.log(error)
      })
    }, []);

  return (
    <ReceiverListContext.Provider value={{ deviceList, updateReceiverListState }}>
      {props.children}
    </ReceiverListContext.Provider>
  )
}
