import { useContext, useEffect, useState } from "react";
import { ReceiverListContext, ReceiverListContextType, ReceiverObjectProps } from "../../State Management/ReceiverListContext";
import { Grid, Typography } from "@mui/material";
import { DeviceBig } from "./DeviceBig";



export const MainPage = () => {
    const { receiverList } = useContext(ReceiverListContext) || {};
    const [receiverTypes, setReceiverTypes] = useState<string[]>([]);

    useEffect(() => {
        if (receiverList) {
            let receiverTypesList: string[] = [];
            receiverList.forEach((receiver) => {
                if (!receiverTypesList.includes(receiver.type)) {
                    receiverTypesList.push(receiver.type)
                }
            })
            setReceiverTypes(receiverTypesList)
        }
    }, [receiverList])



    return (
        <Grid container >
            {receiverTypes.map((receiverType) => {
                return <Grid item xs={12} sm={6} md={2} key={receiverType}>
                    <Typography variant="h6" fontWeight='bold'>{receiverType}</Typography>
                    {receiverList && receiverList.forEach((receiver) => {
                        if (receiver.type === receiverType) {
                            return receiver.IP
                        }
                    })}
                </Grid>
            })}
        </Grid>
    )
}
