import { Typography } from "@mui/material";
import Box from "@mui/material/Box"
import Paper from "@mui/material/Paper";
import { useContext, useEffect, useState } from "react";
import { PingResult, WebsocketPingsContext } from "../../State Management/WebsocketPingsContext";


type DeviceBigProps = {
  id: number;
  name: string;
  IP: string;

}

export const DeviceBig = (props: DeviceBigProps) => {
  const { pingInfo } = useContext(WebsocketPingsContext) || {}
  const [ping, setPing] = useState<number>()

  useEffect(() => {
    if (pingInfo) {
      const pingTime = pingInfo.find(receiver => receiver.host === props.IP)
      setPing(pingTime?.pingTime)
    }
  }, [pingInfo, props.IP])

  return (
    <Box>
      <Paper style={{ margin: '5px', padding: '10px', overflow: 'hidden', display: 'flex', flexDirection: 'row', justifyContent:"space-between"}}>
        <Box display="flex" flexDirection="column" bgcolor={(ping && (ping < 20) ? "green" : "red")}>
          <Typography variant="h6" fontSize={12} fontWeight='bold'>{props.name}</Typography>
          <Typography variant="subtitle1" fontSize={10}>{props.IP}</Typography>
        </Box>
        <Box>
          <Typography fontSize={12}>{ping}</Typography>
        </Box>
      </Paper>
    </Box>
  );
}
