import { useContext, useState } from "react";
import { ReceiverListContext, ReceiverObjectProps } from "../../State Management/ReceiverListContext";
import {  Grid, Typography } from "@mui/material";
import { DeviceBig } from "./DeviceBig";

type ReceiverListByType = {
    [key: string]: ReceiverObjectProps[]
}

export const MainPage = () => {
    const { receiverList } = useContext(ReceiverListContext) || {};
        receiverList?.reduce<ReceiverListByType>((types, receiver) => {
        const type = receiver.type;
        //If type key doesn't exist yet in accumulator (types), add it
        if (!types[type]) {
            types[type] = [];
        }
        if (Boolean(receiver.isSelected)) {
            types[type].push(receiver);
        }
        return types;
    }, {});

    return (
        <Grid container >
            {receiverListByType && Object.entries(receiverListByType).map(([type, receivers]) => (
                <Grid item xs={12} sm={6} md={2} key={type}>
                    <Typography variant="h6" fontWeight='bold'>{type}</Typography>
                    {receivers.map(receiver => (
                        <Grid item xs={12} key={receiver.id} >
                            <DeviceBig key={receiver.id} id={receiver.id} name={receiver.name} IP={receiver.IP}/>
                        </Grid>
                    ))}
                </Grid>
            ))}
        </Grid>
    )
}
