import { stringify } from "querystring";

export const getDeviceList = () => {
    fetch(`${process.env.REACT_APP_BACKEND_URL}`, {
        method: "GET",
      })
        .then((response) => response.json())
        .then((data) => {
          return data
        })
        .catch((error) => {
            return error
        })
};


export const shouldPing = (shouldPing: boolean) => {
  fetch(`${process.env.REACT_APP_BACKEND_URL}`, {
    method: "PUT",
    body: JSON.stringify({ "Monitoring devices": shouldPing })
  })
    .then((response) => response.json())
    .then((data) => {
      return data
    })
    .catch((error) => {
        return error
    })
}
