import React, { createContext, useEffect, useState } from "react";


type receiverObjectProps = {
  id: number;
  name: String;
  ipAddress: String;
  device: String;
}

export const ReceiverListContext = createContext<receiverObjectProps[] | undefined>(undefined);

type ReceiverListProps = {
  children: React.ReactNode;
}

export const ReceiverListProvider = (props: ReceiverListProps) => {
  const [deviceList, setDeviceList] = useState();

    useEffect(() => {
        fetch("http://localhost:3002/", {
          method: "GET",
        })
          .then((response) => response.json())
          .then((data) => {
            setDeviceList(data)
            console.log(data)
          })
          .catch((error) => console.log(error));
      }, []);

      return (
        <ReceiverListContext.Provider value={deviceList}>
          {props.children}
        </ReceiverListContext.Provider>
      )
}
