import React, { createContext, useEffect, useState } from "react";

export type ReceiverObjectProps = {
  id: number;
  name: string;
  IP: string;
  device: string;
  type: string;
  isSelected: string;
}

export type ReceiverListContextType = {
  receiverList: ReceiverObjectProps[] | undefined;
  updateReceiverListState: (newState: ReceiverObjectProps[]) => void;
}

type ReceiverListByType = {
  [key: string]: ReceiverObjectProps[]
}

export const ReceiverListContext = createContext<ReceiverListContextType | undefined>(undefined);

type ReceiverListProps = {
  children: React.ReactNode;
}

export const ReceiverListProvider = (props: ReceiverListProps) => {
  const [receiverList, setReceiverList] = useState<ReceiverObjectProps[] | undefined>();
  const [sortedReceiverList, setSortedReceiverList] = useState

  const updateReceiverListState = (newState: ReceiverObjectProps[]) => {
    setReceiverList(newState)
  }

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = () => {
    fetch(`${process.env.REACT_APP_BACKEND_URL}`, {
      method: "GET",
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data)
        setReceiverList(data)
      })
      .catch((error) => {
        console.log(error)
      })
  }

  const sortReceivers = () => {
    receiverList?.reduce<ReceiverListByType>((types, receiver) => {
      const type = receiver.type;
      //If type key doesn't exist yet in accumulator (types), add it
      if (!types[type]) {
          types[type] = [];
      }
      if (Boolean(receiver.isSelected)) {
          types[type].push(receiver);
      }
      return types;
  }, {});
  }

  return (
    <ReceiverListContext.Provider value={{ receiverList, updateReceiverListState }}>
      {props.children}
    </ReceiverListContext.Provider>
  )
}
