import { Button, Container, CssBaseline, Divider, Drawer, IconButton, Toolbar, Typography } from '@mui/material';
import MuiAppBar, { AppBarProps as MuiAppBarProps } from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import { FormEvent, useContext, useState } from 'react';
import MenuIcon from '@mui/icons-material/Menu';
import { styled } from '@mui/material/styles';
import ChevronLeftIcon from '@mui/icons-material/ChevronLeft';
import { MainPage } from '../pages/monitorReceivers/mainPage';
import { ReceiverListContext } from '../State Management/ReceiverListContext';


const drawerWidth = 240;

const Main = styled('main', { shouldForwardProp: (prop) => prop !== 'open' })<{
  open?: boolean;
}>(({ theme, open }) => ({
  flexGrow: 1,
  padding: theme.spacing(3),
  transition: theme.transitions.create('margin', {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),

  ...(open && {
    transition: theme.transitions.create('margin', {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
    marginLeft: drawerWidth,
  }),
}));

interface AppBarProps extends MuiAppBarProps {
  open?: boolean;
}

const AppBar = styled(MuiAppBar, {
  shouldForwardProp: (prop) => prop !== 'open',
})<AppBarProps>(({ theme, open }) => ({
  transition: theme.transitions.create(['margin', 'width'], {
    easing: theme.transitions.easing.sharp,
    duration: theme.transitions.duration.leavingScreen,
  }),
  ...(open && {
    width: `calc(100% - ${drawerWidth}px)`,
    marginLeft: `${drawerWidth}px`,
    transition: theme.transitions.create(['margin', 'width'], {
      easing: theme.transitions.easing.easeOut,
      duration: theme.transitions.duration.enteringScreen,
    }),
  }),
}));

const DrawerHeader = styled('div')(({ theme }) => ({
  display: 'flex',
  alignItems: 'center',
  padding: theme.spacing(0, 1),
  // necessary for content to be below app bar
  ...theme.mixins.toolbar,
  justifyContent: 'flex-end',
}));



export const MainTemplate = () => {
  const [open, setOpen] = useState(false)
  const [isPinging, setisPinging] = useState<boolean>(false)
  const { receiverList, updateReceiverListState } = useContext(ReceiverListContext) || {};
  const [ formState, setFormState ] = useState<Number[]>()

  const handleDrawerOpen = () => {
    setOpen(true)
  }

  const handleDrawerClose = () => {
    setOpen(false)
  }


  const handleClick = () => {
    fetch(`${process.env.REACT_APP_BACKEND_URL}/shouldPing`, {
      method: "PUT",
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ "shouldPing": !isPinging })
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        setisPinging(data["Monitoring devices"]);
        if (updateReceiverListState) {
          updateReceiverListState(data)
        }

      })
      .catch((error) => {
        console.error('There was a problem with the fetch operation:', error);
      });
  }

  const handleSubmit = (event: FormEvent<HTMLFormElement>) => {
    event.preventDefault();
  }



  return (
    <Box>
      <CssBaseline />
      <AppBar position="fixed" open={open}>
        <Toolbar>
          <IconButton
            color="inherit"
            aria-label="open drawer"
            onClick={handleDrawerOpen}
            edge="start"
            sx={{ mr: 2, ...(open && { display: 'none' }) }}
          >

            <MenuIcon />
            <Typography padding={1}>Device List</Typography>
          </IconButton>
          <Button variant='contained' color='error' onClick={handleClick}>{isPinging ? "STOP" : "START"} Monitoring</Button>
        </Toolbar>
      </AppBar>
      <Drawer
        sx={{
          width: drawerWidth,
          flexShrink: 0,
          '& .MuiDrawer-paper': {
            width: drawerWidth,
            boxSizing: 'border-box',
          },
        }}
        variant="persistent"
        anchor="left"
        open={open}
      >
        <DrawerHeader>
          <Typography variant="h6" >Device List</Typography>
          <IconButton onClick={handleDrawerClose}>
            <ChevronLeftIcon />
          </IconButton>
        </DrawerHeader>
        <Divider />
        <form onSubmit={handleSubmit}>
          {receiverList?.map(receiver => {
            return (<><input value={receiver.id} onChange={() => setFormState(...formState, receiver.id)} type='checkbox' name={receiver.name} key={receiver.id} id={String(receiver.id)} />
                    <label htmlFor={receiver.id.toString()}>{receiver.name}</label><br/></>)
          }
          )}
        </form>
      </Drawer>
      <Main open={open}>
        <DrawerHeader />
        <Container maxWidth="xl">

          <MainPage />

        </Container>
      </Main>
    </Box>
  )
}
