import { createContext, useEffect, useState } from "react";
import useWebSocket, { ReadyState } from 'react-use-websocket';

export type PingResult = {
    id: number;
    ping: number;
}

export type WebsocketPingsContextType = {
    pingInfo: PingResult[] | undefined;

}

export const WebsocketPingsContext = createContext<WebsocketPingsContextType | undefined>(undefined)


export const WebsocketPingsProvider = (children: React.ReactNode) => {
    const [pingInfo, setPingInfo] = useState<PingResult[] | undefined | any>();
    const { lastMessage, readyState } = useWebSocket("ws://localhost:3001",{
        share: false,
        shouldReconnect: () => true,
      },);

    const connectionStatus = {
        [ReadyState.CONNECTING]: 'Connecting',
        [ReadyState.OPEN]: 'Open',
        [ReadyState.CLOSING]: 'Closing',
        [ReadyState.CLOSED]: 'Closed',
        [ReadyState.UNINSTANTIATED]: 'Uninstantiated',
      }[readyState];

    useEffect(()=> {

            setPingInfo(lastMessage)
            console.log("Websocket connection status: ", connectionStatus)


    }, [lastMessage, readyState, connectionStatus])

    return (
        <WebsocketPingsContext.Provider value={{ pingInfo }}>
            {children}
        </WebsocketPingsContext.Provider>
    )
}
